import React from 'react'
import './About.css'
import './certificate.css'
import './Project.css';


import q from '../image/Important/1.jpg';
import w from '../image/Important/2.jpg';
import e from '../image/Important/3.jpg';
import r from '../image/Important/4.jpg';
import t from '../image/Important/5.jpg';
import y from '../image/Important/6.jpg';
import u from '../image/Important/7.jpeg';

import CardItem from './CardItem';
import Footer from '../Footer';

function certificate() {
    var scrollTop = function () {
        window.scrollTo(0, 0);
    };
    return(
    <div className='cards'>
        <h1 style={{ fontFamily: 'monospace', color: 'white' }}>PROJECT</h1>
        <div style={{ alignItems: 'center', justifyContent: 'center', display: 'flex', paddingBottom: 20 }}>
            <hr
                style={{
                    width: 50,
                    color: 'red',
                    backgroundColor: 'red',
                    height: 5
                }}
            />
        </div>

        <div className='cards__container'>
            <h2 style={{ fontFamily: 'monospace', color: 'white' }}>Python Project</h2>
            <div className='cards__wrapper'>
                <CardItem
                    src={python}

                />
                <CardItem
                    src={python1}

                />
            </div>
        </div>
        <hr
            style={{
                color: 'red',
                backgroundColor: 'red',
                height: 5
            }}
        />
        <div className='cards__container'>
            <h2 style={{ fontFamily: 'monospace', color: 'white' }}>React.js Project</h2>
            <div className='cards__wrapper'>

                <CardItem
                    src={dum1}

                />

                <CardItem
                    src={dum3}

                />

            </div>
        </div>
        <hr
            style={{
                color: 'red',
                backgroundColor: 'red',
                height: 5
            }}
        />

        <div className='cards__container'>
            <h2 style={{ fontFamily: 'monospace', color: 'white', paddingBottom: 10 }}>React Native Project</h2>
            <h3 style={{ fontFamily: 'monospace', color: 'white', paddingTop: 20 }}>1-Social Media</h3>
            <div className='cards__wrapper'>

                <CardItem
                    src={social_media1}

                />

                <CardItem
                    src={social_media2}

                />

            </div>
            <h3 style={{ fontFamily: 'monospace', color: 'white', paddingTop: 20 }}>2-Photo Feed Application</h3>
            <div className='cards__wrapper'>

                <CardItem
                    src={photo_feed1}

                />

                <CardItem
                    src={photo_feed2}

                />

            </div>

            <h3 style={{ fontFamily: 'monospace', color: 'white', paddingTop: 20 }}>3-Libary Management System</h3>
            <div className='cards__wrapper'>

                <CardItem
                    src={libary}

                />

                <CardItem
                    src={libary_q}

                />

            </div>
            <h3 style={{ fontFamily: 'monospace', color: 'white', paddingTop: 20 }}>4-Firebase Authentication </h3>
            <div className='cards__wrapper'>

                <CardItem
                    src={fbauth}

                />

                <CardItem
                    src={fbauth1}

                />

            </div>
            <hr
                style={{
                    color: 'red',
                    backgroundColor: 'red',
                    height: 5
                }}
            />
            <div className='cards__container'>
                <h2 style={{ fontFamily: 'monospace', color: 'white' }}>5-Kivy Project</h2>
                <div className='cards__wrapper'>

                    <CardItem
                        src={kivy1}

                    />

                    <CardItem
                        src={kivy2}

                    />

                </div>
            </div>
        </div>
    </div>
    )
}

export default certificate
