import React from 'react'
import './About.css'
import bg from '../image/me3.png';

const About = () => {
    return (
        <div className='first_div'>
            <h1 style={{color:"white",position: 'absolute',paddingTop:'20%' }}>Text over image</h1>
            <img
                style={{backgroundSize : 'cover',width:'100%'}} responsive
                src={bg}>
            </img>
        </div>


    )
}
export default About;
