import React from 'react'
import './About.css'
import bg from '../image/me3.png';

const About = () => {
    return (
        <div style={{ alignItems: 'center', justifyContent: 'center', width: '100%' }}>
            <div className='first_div'>
                <img src={bg} style={{ width: '100%', height: '100%' }} />
                <div style={{position: 'flex', top: 0, left: 0, right: 0, bottom: 0, justifyContent: 'center', alignItems: 'center'}}>
                    <text style={{ color: 'white',}}>Shashwat</text>
                </div>

            </div>
        </div>


    )
}
export default About;
