import React from 'react'
import './About.css'
import bg from '../image/me3.png';

const About = () => {
    return (
        <div style={{ alignItems: 'center', justifyContent: 'center', width: '100%' }}>
            <div style={{alignItems:'center',justifyContent:'center',display:'flex',paddingBottom:'50%'}}>
                <img src={bg} style={{ width: '100%', height: '100%' }} />
                <text style={{ color: 'white',}}>Shashwat</text>
            </div>
        </div>


    )
}
export default About;
