import React from 'react'
import './About.css'
import videoq from '../image/vd.mp4'
import me from '../image/me.jpeg';
import bgg from '../image/me3.png';
import android from '../image/Android.jpg'
import db from '../image/db.jpg';
import web from '../image/web.jpg';
import firebase from '../image/firebase.png';
import node from '../image/nodeja.png';
import reactjs from '../image/favicon.ico';
import python from '../image/python.png';
import native from '../image/reactnative.png'
import './Project.css';


import q from '../image/Important/1.jpg';
import w from '../image/Important/2.jpg';
import e from '../image/Important/3.jpg';
import r from '../image/Important/4.jpg';
import t from '../image/Important/5.jpg';
import y from '../image/Important/6.jpg';
import u from '../image/Important/7.jpeg';

import CardItem from './CardItem';
import Footer from '../Footer';

function certificate() {
    return (
        <div>
            <div className='third_div'>
                <h1 className='header'>SHASHWAT ANAND</h1>
                <video className='videoq' src={videoq} autoPlay loop muted />
            </div>
            <hr
                style={{
                    color: 'white',
                    backgroundColor: 'white',
                    height: 5,
                }}
            />
            <div className='first_div'>
                <h1 style={{ paddingTop: '8%', color: 'white', paddingBottom: -5, fontFamily: 'Amatic SC' }}>ABOUT ME</h1>
                <div style={{ alignItems: 'center', justifyContent: 'center', display: 'flex' }}>
                    <hr
                        style={{
                            color: 'red',
                            backgroundColor: 'red',
                            height: 5,
                            width: '30%'
                        }}
                    />
                </div>
                <div className='skill'>
                
                    <div className='cards_about' >
                        <h1 style={{ fontFamily: 'monospace', color: 'white', fontFamily: 'Amatic SC' }}>certificate</h1>
                        <div style={{ alignItems: 'center', justifyContent: 'center', display: 'flex', paddingBottom: 15 }}>
                            <hr
                                style={{
                                    color: 'red',
                                    backgroundColor: 'red',
                                    height: 5,
                                    width: '35%',

                                }}
                            />
                        </div>
                        <div className='cards__container_about'>
                            <ul className='cards__items'>
                                <CardItem
                                src={t}
                                text='Android Developement'

                                path='/services'
                            />

                            </ul>
                        <ul className='cards__items'>
                            <CardItem
                                src={u}
                                text='Android Developement'

                                path='/services'
                            />

                        </ul>
                        <ul className='cards__items'>
                            <CardItem
                                src={y}
                                text='Web Developement'
                                path='/services'
                            />

                        </ul>
                        <ul className='cards__items'>
                            <CardItem
                                src={q}
                                text='Database management like Firebase or SQL'
                                path='/services'
                            />

                        </ul>
                        <ul className='cards__items'>
                            <CardItem
                                src={w}
                                text='Android Developement'

                                path='/services'
                            />

                        </ul>
                        <ul className='cards__items'>
                            <CardItem
                                src={e}
                                text='Web Developement'
                                path='/services'
                            />

                        </ul>
                        <ul className='cards__items'>
                            <CardItem
                                src={r}
                                text='Database management like Firebase or SQL'
                                path='/services'
                            />

                        </ul>

                    </div>
                </div>
            </div>
            <div style={{ width: '100%', margin: 0 }}>
                <Footer />
            </div>
        </div>
        </div >

    )
}

export default certificate
