import React from 'react'
import './About.css'
import bg from '../image/me3.png';

const About = () => {
    var textStyle = {
        position: 'absolute', 
        top: '50%', 
        left: '50%',
        color:'white'
      };
    return (
        <div className='first_div'>
            <h1 style={textStyle}>Text over image</h1>
            <img
                style={{backgroundSize : 'cover',width:'100%'}} responsive
                src={bg}>
            </img>
        </div>


    )
}
export default About;
